.btn,
button,
button[class] {
  display: inline-block;
  padding: 0;
  height: 2.25rem;
  line-height: 2.25rem;
  padding: 0 1rem;
  background: #263238;
  transition: box-shadow 250ms cubic-bezier(0.4, 0, 0.2, 1), background 250ms cubic-bezier(0.4, 0, 0.2, 1);
  text-transform: uppercase;
  color: rgba(170, 187, 195, 0.8);
  outline: none;
  border: none;
  margin: 0.25rem 0;
  vertical-align: middle;
  box-shadow: 0 1px 4px rgba(0, 0, 0, 0.12), 0 1px 4px rgba(0, 0, 0, 0.24);
}
.btn:hover,
button:hover,
button[class]:hover {
  color: rgba(170, 187, 195, 0.8);
  background: #263238;
  box-shadow: 0 3px 6px rgba(0, 0, 0, 0.16), 0 3px 6px rgba(0, 0, 0, 0.23);
